    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String key = jTextField3.getText();
        key = deleteSpace(key);

        String plainText = jTextField1.getText();
        plainText = deleteSpace(plainText);
        int[] numKey = keyWord(key);

        int residualcheck = plainText.length() % key.length(); // kiem tra co du khong
        int dummyCharacters = key.length() - residualcheck; // lay key tru cho so du de coi them bao nhieu ky tu cho bang cot theo key
        if (residualcheck != 0) {
            for (int i = 0; i < dummyCharacters; i++) {
                plainText += "z";
            }
        }

        int numRow = plainText.length() / key.length(); //sau khi them ky tu / cho key de xem tao ra bao nhieu dong
        char[][] arr = new char[numRow][key.length()];
        int z = 0;
        for (int i = 0; i < numRow; i++) {                    //them cac ki tu plaintext vao ma tran
            for (int j = 0; j < key.length(); j++) {
                arr[i][j] = plainText.charAt(z++);
            }
        }

        String cipherText = "";//tao chuoi rong
        String numLoc = getNumber(key, numKey);
        int num = 0;//tao bien n voi gia tri = 0
        for (int i = 0; i < key.length(); i++) { //Duyet theo cot
            int d;
            if (num == key.length()) { //neu k = do dai key thi thoat vong lap
                break;
            } else {
                d = Character.getNumericValue(numLoc.charAt(num++));//Character.getNumericValue chuyen tu dang chu => so

            }
            for (int j = 0; j < numRow; j++) { //Them ki tu vao chuoi rong theo cot
                cipherText += arr[j][d];
            }
        }
        jTextField2.setText(cipherText);
        jTextField1.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        String cipherText = jTextField2.getText();
        cipherText = deleteSpace(cipherText);
        String key = jTextField3.getText();
        key = deleteSpace(key);
        int numRow = cipherText.length() / key.length(); //tao so dong
        char[][] arr = new char[numRow][key.length()];
        int[] numList = keyWord(key);
        String numLoc = getNumber(key, numList);
        for (int i = 0, k = 0; i < cipherText.length(); i++) { //khoi tao for theo do dai cua cipherText va khoi tao k = 0;
            int d = 0;//dung de gan so k
            if (k == key.length()) {
                k = 0;
            } else {
                d = Character.getNumericValue(numLoc.charAt(k++)); //Dung phuong thuc de chuyen ki tu trong ham numLoc sang int
            }

            for (int j = 0; j < numRow; j++) { //Them ki tu vao ma tran tu vi tri chu nho nhat den tang dan

                arr[j][d] = cipherText.charAt(i++);
            }
            --i;
        }
        String plainText = "";//Tao chuoi rong
        for (int i = 0; i < numRow; i++) { //Duyet dong
            for (int j = 0; j < key.length(); j++) {//Duyet cot
                plainText += arr[i][j];//Them ki tu da duyet tu dong va cot them vao chuoi
            }
        }
        jTextField1.setText(plainText);
        jTextField2.setText("");
    }//GEN-LAST:event_jButton2ActionPerformed

