    public static int[] keyWord(String key)//mang theo bang chu cai tai phan tu mang vi du tu hello => 2 1 3 4 5
    {
        String alpha = "abcdefghijklmnopqrstuvwxyz"; //tao bang chu cai tu a-z
        int[] numList = new int[key.length()]; //mang do dai cua mang tu key
        int a = 0;
        for (int i = 0; i < alpha.length(); i++) {
            for (int j = 0; j < key.length(); j++) {
                if (alpha.charAt(i) == key.charAt(j)) { //neu ma chu cai do == chu cai trong key   
                    a++;
                    numList[j] = a; //gan phan tu do = a
                    //sau do tang len 1
                }
            }
        }
        return numList;
    }

    public static String getNumber(String key, int[] numList)// chuoi dung de sap xep phan tu tu ham keyword
    {
        String a = "";//tao chuoi rong

        for (int i = 0; i < key.length() + 1; i++) { //cong them 1 vi trong ham keyWord t chay tu 1-n
            for (int j = 0; j < key.length(); j++) {
                if (numList[j] == i) { // tu trong numList tao tren ta dung 2 vong lap de dim phan tu numList == i 
                    a += j;//sau do gan vi tri tim duoc vao a

                }
            }
        }
        return a;
    }

    public static String deleteSpace(String text) {
        text = text.toLowerCase();
        text = text.replace(" ", "");
        String result = "";//tạo chuỗi rỗng
        for (int i = 0; i < text.length(); i++) { // dùng vòng lặp for để duyệt lấy kí tự
            char c = text.charAt(i); //dùng kiểu dữ liệu char để lấy một kí tự trong chuỗi string dùng charAt
            if (Character.isLetter(c)) { // kiểm tra có phải là kí tự không
                result += c; // nếu là kí tự thì thêm vào chuỗi
            }
        }
        return result;
    }

